import axios from 'axios';
import React, { useState } from 'react'
import { Link, useHistory } from "react-router-dom";

export function Login() {
    const history = useHistory();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const Login = () => {
        const obj = {
            username: username,
            password: password
        }
        axios.post('http://localhost:8000/login', obj).then(
            (res) => {
                console.log(res)
                history.push("/home")
            }
        ).catch((err) => {
            console.error(err);
        })
    }
    const imgUrl = "https://www.mayoclinichealthsystem.org/-/media/national-files/images/hometown-health/2019/running-shoes-on-paved-trail.jpg?h=370&w=660&la=en&hash=EF0A1D8B952FBA06002A40167735BF14";

    return (
        <div>
            <div className="row no-gutters">
                <div className="col-md-5">
                    <div className="leftside">
                        <img className="img-fluid" src={imgUrl} />
                    </div>
                </div>
                <div className="col-md-7 login-sub-wrapper">
                    <div className="rightside">
                        <div className="login-sub-wrapper">
                            <h2 className="text-center">Login</h2>
                            <div className="pt-4 login-box">
                                <div className="mb-3">
                                    <label htmlFor="usename" className="form-label">
                                        Username
      </label>
                                    <input
                                        value={username}
                                        onChange={(e) => { setUsername(e.target.value) }}
                                        type="text"
                                        className="form-control"
                                        id="username"
                                        placeholder="Enter username"
                                    />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="password" className="form-label">
                                        Password
      </label>
                                    <input
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                        type="password"
                                        className="form-control"
                                        id="password"
                                        placeholder="Password"
                                    />
                                </div>
                                <div className="col-12">
                                    <button className="btn btn-primary" onClick={() => Login()} >
                                        Login
      </button>
                                    <button className="btn btn-sucess ml-3">
                                        <Link to="/signup">Register</Link>
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
